import os
import numpy as np

def convert(el):
    if el == 0:
        el = -2
    elif (el == 1):
        el = -1
    elif (el == 2):
        el = 0
    elif (el == 3):
        el = 1
    else:
        el = 2
    return el

alcatraz = ""
alcatraz = list(alcatraz)

insularis = ""
insularis = list(insularis)

jararaca = ""
jararaca = list(jararaca)


matrix = [alcatraz, insularis, jararaca]

f = open("matrix.mat", "x")
for row in np.transpose(matrix):
    line = ""
    for el in row:
        line = line + str(convert(int(el))) + " "
    f.write(line[:-1] + "\n")
f.close()



f = open("protein.mat", "x")
for i in range(len(alcatraz)):
    f.write(str(i) + "\n")
f.close()

